mapper.update:
UPDATE ems_class c LEFT JOIN ems_profession p ON c.professionId_n = p.professionId_n
SET c.active_c = 'I'
WHERE p.facultyId_n = #{facultyId, jdbcType=INTEGER}

UPDATE ems_class c LEFT JOIN ems_profession p ON c.professionId_n = p.professionId_n
SET c.active_c = 'A'
WHERE p.facultyId_n = #{facultyId, jdbcType=INTEGER}
