mapper.select:
SELECT COUNT(1) FROM ems_class c
LEFT JOIN ems_profession p ON c.professionId_n = p.professionId_n
WHERE 1 = 1

    AND p.facultyId_n = #{facultyId, jdbcType=INTEGER}


    AND c.professionId_n = #{professionId, jdbcType=INTEGER}


    AND c.grade_m = #{grade, jdbcType=INTEGER}

SELECT c.classId_n AS clazzId, c.grade_m AS gradeName, c.class_m AS clazzName, c.professionId_n AS professionId, c.active_c AS active, c.create_dt AS createDate, c.create_m AS createBy, f.name_m AS facultyName, f.active_c AS facultyActive, p.name_m AS professionName, p.active_c AS professionActive FROM ems_class c LEFT JOIN ems_profession p ON c.professionId_n = p.professionId_n LEFT JOIN ems_faculty f ON p.facultyId_n = f.facultyId_n WHERE 1 = 1
AND p.facultyId_n = #{facultyId, jdbcType=INTEGER}
AND c.professionId_n = #{professionId, jdbcType=INTEGER}
AND c.grade_m = #{grade, jdbcType=INTEGER}
LIMIT #{pagination.begin}, #{pagination.limit}
