mapper.select:
SELECT f.facultyId_n AS facultyId, f.name_m AS name, f.active_c AS active, f.create_dt AS createDate, f.create_m AS createBy, f.update_dt AS updateDate, f.update_m AS updateBy FROM ems_faculty f WHERE active_c = 'A'
AND name_m LIKE CONCAT('%', #{facultyName, jdbcType=VARCHAR}, '%')
LIMIT #{pagination.begin}, #{pagination.limit}
