mapper.select:
SELECT COUNT(1)
FROM ems_profession

    WHERE facultyId_n = #{facultyId, jdbcType=INTEGER}

SELECT professionId_n AS professionId, facultyId_n AS facultyId, name_m AS name, create_dt AS createDate, create_m AS createBy, update_dt AS updateDate, update_m AS updateBy FROM ems_profession
WHERE facultyId_n= #{facultyId, jdbcType=INTEGER}
LIMIT #{pagination.begin}, #{pagination.limit}

mapper.delete:
DELETE p1 FROM ems_profession p1
LEFT JOIN ems_faculty f1 ON p1.facultyId_n = f1.facultyId_n
WHERE f1.facultyId_n = #{facultyId, jdbcType=INTEGER}
