mapper.select:
SELECT subjectId_n AS subjectId, name_m AS name, active_c AS active, create_dt AS createDate, create_m AS createBy, DATE_FORMAT(create_dt, '%Y-%m-%d') AS createDateStr FROM ems_subject WHERE 1 =1
AND UPPER(name_m) LIKE UPPER(CONCAT('%', #{subjectName, jdbcType=VARCHAR}, '%'))
LIMIT #{pagination.begin}, #{pagination.limit}
